import React, { useState, useEffect } from 'react';
import { getAllVersions } from '../services/api';

const VersionList: React.FC = () => {
    const [versions, setVersions] = useState<Version[]>([]);
    const [sortBy, setSortBy] = useState<string>('id');
    const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

    // 加载版本数据
    const loadVersions = async () => {
        try {
            const data = await getAllVersions();
            setVersions(data);
        } catch (error) {
            console.error('加载版本失败', error);
        }
    };

    useEffect(() => {
        loadVersions();
    }, []);

    // 排序函数
    const handleSort = (column: string) => {
        if (sortBy === column) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortBy(column);
            setSortOrder('asc');
        }
    };

    const sortedVersions = [...versions].sort((a, b) => {
        const valueA = a[sortBy as keyof Version];
        const valueB = b[sortBy as keyof Version];
        if (typeof valueA === 'string' && typeof valueB === 'string') {
            return sortOrder === 'asc' ? valueA.localeCompare(valueB) : valueB.localeCompare(valueA);
        } else if (typeof valueA === 'number' && typeof valueB === 'number') {
            return sortOrder === 'asc' ? valueA - valueB : valueB - valueA;
        }
        return 0;
    });

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th onClick={() => handleSort('id')}>ID</th>
                        <th onClick={() => handleSort('name')}>名称</th>
                        <th onClick={() => handleSort('priority')}>优先级</th>
                        {/* 其他列 */}
                    </tr>
                </thead>
                <tbody>
                    {sortedVersions.map(version => (
                        <tr key={version.id}>
                            <td>{version.id}</td>
                            <td>{version.name}</td>
                            <td>{version.priority}</td>
                            {/* 其他列 */}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default VersionList;
